class PranxToast extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){const message=this.getAttribute('message')||'This is a toast message.';this.shadowRoot.innerHTML=`<div class="toast">${message}</div>`;setTimeout(()=>this.remove(),3000);}
}customElements.define('pranx-toast',PranxToast);

class PranxTooltip extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){const text=this.getAttribute('text')||'Tooltip text';this.shadowRoot.innerHTML=`<span class="tooltip">${text}</span>`;}}
customElements.define('pranx-tooltip',PranxTooltip);

class PranxDrawer extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){this.shadowRoot.innerHTML=`<div class="drawer"><slot></slot></div>`;}}
customElements.define('pranx-drawer',PranxDrawer);

class PranxSwitch extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){this.shadowRoot.innerHTML=`<label class="switch"><input type="checkbox"><span class="slider"></span></label>`;}}
customElements.define('pranx-switch',PranxSwitch);

class PranxButton extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){const label=this.getAttribute('label')||'Click Me';this.shadowRoot.innerHTML=`<button>${label}</button>`;}}
customElements.define('pranx-btn',PranxButton);

class PranxInput extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){const placeholder=this.getAttribute('placeholder')||'Enter text';this.shadowRoot.innerHTML=`<input type="text" placeholder="${placeholder}">`;}
}customElements.define('pranx-input',PranxInput);

class PranxSelect extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){const options=this.innerHTML.trim();if(!options){console.warn('No options provided for <pranx-select>.');}this.shadowRoot.innerHTML=`<select>${options}</select>`;}
}customElements.define('pranx-select',PranxSelect);

class PranxList extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){const items=this.getAttribute('items')||'';this.shadowRoot.innerHTML=`<ul>${items}</ul>`;}
}customElements.define('pranx-list',PranxList);

class PranxSlider extends HTMLElement{constructor(){super();this.attachShadow({mode:'open'});}connectedCallback(){this.shadowRoot.innerHTML=`<input type="range" min="1" max="100" value="50">`;}
}customElements.define('pranx-slider',PranxSlider);
